#!/usr/bin/perl -p
#
# the -p option makes this a pass-through filter
#####################################################

# omit protected region
next if /^#\+BEGIN_EXAMPLE/ .. /^#\+END_EXAMPLE/;

# `some text`                      ⇒   =some text=
s: ` ( [^`]* ) `                       :=$1=:gx;

# [desc](link)                     ⇒   [[link][desc]]
s: \[ ( [^]]* ) \] \( ( [^)]* ) \)     :[[$2][$1]]:gx;

# ^## some heading ⇒ ** some heading
#      NB: can't use /x here or would have to use ugly \#
s:^##:**:;   

# *some italics*                   ⇒   /some italics/
s: (?!< \* ) \* ( [^*]+ ) \* (?! \*)   :/$1/:gx;

# **some bold**                    ⇒   *some bold*
s: \*{2} ( [^*]+ ) \*{2}               :*$1*:gx;

#!/usr/bin/env python3.2
from __future__ import print_function
import sys import re
if (sys.version_info[0] == 2): sys.stderr.write("%s: legacy Python detected! Please upgrade to v3+\n" % sys.argv[0] ) ##sys.exit(2)
if len(sys.argv) == 1: sys.argv.append("/dev/stdin")
flip_rx = re.compile(r'^#\+BEGIN_EXAMPLE') flop_rx = re.compile(r'^#\+END_EXAMPLE')
#EG# `some text` --> =some text= lhs_backticks = re.compile(r'` ( [^`]* ) `', re.VERBOSE) rhs_backticks = r'=\1='
#EG# [desc](link) --> [[link][desc]] lhs_desclink = re.compile(r' \[ ( [^]]* ) \] \( ( [^)]* ) \) ', re.VERBOSE) rhs_desclink = r'[[\2][\1]]'
#EG# ^## some heading --> ** some heading lhs_header = re.compile(r'^##') rhs_header = r'**'
#EG# *some italics* --> /some italics/ lhs_italics = re.compile(r' (?!< \* ) \* ( [^*]+ ) \* (?! \*) ', re.VERBOSE) rhs_italics = r'/\1/'
## **some bold** --> *some bold* lhs_bold = re.compile(r'\*{2} ( [^*]+ ) \*{2}', re.VERBOSE) rhs_bold = r'*\1*'
errcnt = 0
flipflop = "flip"
for filename in sys.argv[1:]: try: filehandle = open(filename, "r") except IOError as oops: errcnt = errcnt + 1 sys.stderr.write("%s: can't open '%s' for reading: %s\n" % ( sys.argv[0], filename, oops) ) else: try: for line in filehandle:
new_flipflop = None
if flipflop == "flip": if flip_rx.search(line): new_flipflop = "flop" elif flipflop == "flop": if flop_rx.search(line): new_flipflop = "flip" else: raise FlipFlop_SNAFU
if flipflop != "flop": line = lhs_backticks . sub ( rhs_backticks, line) line = lhs_desclink . sub ( rhs_desclink, line) line = lhs_header . sub ( rhs_header, line) line = lhs_italics . sub ( rhs_italics, line) line = lhs_bold . sub ( rhs_bold, line) print(line, end="")
if new_flipflop != None: flipflop = new_flipflop
except IOError as oops: errcnt = errcnt + 1 sys.stderr.write("%s: can't read '%s': %s\n" % ( sys.argv[0], filename, oops) ) finally: try: filehandle.close() except IOError as oops: errcnt = errcnt + 1 sys.stderr.write("%s: can't close '%s': %s\n" % ( sys.argv[0], filename, oops) )
if errcnt == 0: sys.exit(0) else: sys.exit(1)

