def ack5(m, n):
    if m == 0: return n + 1
    if m == 1: return n + 2
    if m == 2: return 2*n + 3
    if m == 3: return 8*(2**n - 1) + 5
    if n == 0: return ack5(m-1, 1)
    return ack5(m-1, ack5(m, n-1))

