def query_in_string(query, string):
    for term in query.split('OR'):
        lst = map(str.strip, term.split('AND'))
        if all(word in string for word in lst):
           return True
    return False

def query_in_string(query, string):
    return any(all(word.strip() in string for word in term.split('AND'))
               for term in query.split('OR'))

def removeNonPrimes(palindromes):
    for palindrom in palindromes:
        if isPrime(palindrom):
            palindromes.remove(palindrom)
    return palindromes

def title(string):
    result = []
    prev_letter = ' '

    for ch in string:
        if not prev_letter.isalpha():
            result.append(ch.upper())
        else:
            result.append(ch.lower())

        prev_letter = ch

    return "".join(result)

