class RevealAccess(object): """A data descriptor that sets and returns values normally and prints a message logging their access. """ def __init__(self, initval=None, name='var'): self.val = initval self.name = name def __get__(self, obj, objtype): print 'Retrieving', self.name return self.val def __set__(self, obj, val): print 'Updating' , self.name self.val = val
class MyClass(object): x = RevealAccess(10, 'var "x"') y = RevealAccess(5, 'var "y"')
m = MyClass() m.x m.x = 20 m.x m.y

class Stored(object): """A data descriptor that stores instance values in itself. """ instances = dict() def __init__(self, val): self.instances[self, None] = val def __get__(self, obj, objtype): return self.instances[self, obj] def __set__(self, obj, val): self.instances[self, obj] = val
class MyClass(object): x = Stored(3) y = Stored(9)
print(MyClass.x) print(MyClass.y) m = MyClass() m.x = 42 print(m.x) m.y = 19 print(m.y) print(m.x)

from suds.plugin import MessagePlugin
class UnicodeFilter(MessagePlugin):
    def received(self, context):
        decoded = context.reply.decode('utf-8', errors='ignore')
        reencoded = decoded.encode('utf-8')
        context.reply = reencoded

