def parse_line(line): brk = False kv = {} for part in line.split(','): if part == 'BREAK_L': brk = True else: k = part[:2] v = part[2:] kv[k] = v return (brk,kv)
def parse_survey(survey): ig1 = itemgetter('PN','LA','LN','EL') ig2 = itemgetter('PN','LA','LN') brk_data = None for line in survey.strip().splitlines(): brk, data = parse_line(line) if brk: brk_data = data continue else: yield ig1(data) + ig2(brk_data)
