import re

def explode(s, keywords):
    for k in keywords:
        m = re.search(r'(%s~[^~]*)(?:~|$)' % (re.escape(k),), s)
        yield m and m.group(1)

for row in lst:
    row[1:2] = explode(row[1], "Mega Mid Micro".split())

import re from pprint import pprint
def explode(s, keywords): for k in keywords: m = re.search(r'(%s~[^~]*)(?:~|$)' % (re.escape(k),), s) yield m and m.group(1)
lst = [ ['171000', 'Thing..Mega~Corporate~Thing..Mid~Dairy~Thing..Micro~Cheese', 'Cheese', '0.012174'], ['171000', 'Thing..Mega~Corporate', 'Cheese', '0.012174'], ]
print("Before:") pprint(lst)
for row in lst: row[1:2] = explode(row[1], "Mega Mid Micro".split())
