from django.db import models from django.core import exceptions import re
MONEY_REGEX_PATTERN = re.compile('^\$?-?0*(?:\d+(?!,)(?:\.\d{1,2})?|(?:\d{1,3}(?:,\d{3})*(?:\.\d{1,2})?))$')
def MoneyValidator(value): """Will validate a string value against a money regular expression""" if not MONEY_REGEX_PATTERN.match(value): raise exceptions.ValidationError('{v} is not a valid dollar amount'.format(v=value))
class MyMoneyField(models.CharField):
"""Custom field for handling values from PostgreSQL's money data type"""
def __init__(self, *args, **kwargs): kwargs['validators'] = [MoneyValidator] kwargs['max_length'] = 20 super(MyMoneyField, self).__init__(*args, **kwargs)

