def OnStart(self, event): #START BUTTON self.richTextCtrl1.AppendText("START\n") if not self.worker: self.worker = WorkerThread(self) self.worker.start()
def OnStop(self, event): #STOP BUTTON self.richTextCtrl1.AppendText("STOP\n") if self.worker: self.worker.stop()
class WorkerThread(threading.Thread): def __init__(self, notify_window): threading.Thread.__init__(self) self.counter = 0 self._notify_window = notify_window self.abort = False
def run(self): while not self.abort: self.counter += 1 wx.PostEvent(self._notify_window, DataEvent(self.counter)) time.sleep(1)
def stop(self): self.abort = True
