def populate(cu): cu.execute(""" CREATE TABLE feature ( -- 300k feature_id INTEGER PRIMARY KEY, mzMin DOUBLE, mzMax DOUBLE, rtMin DOUBLE, rtMax DOUBLE, lnk_feature INT); CREATE TABLE spectrum ( -- 20k spectrum_id INTEGER PRIMARY KEY, mz DOUBLE, rt DOUBLE, lnk_spectrum INT);""") cu.execute("BEGIN") for i in range(300000): ((mzMin, mzMax), (rtMin, rtMax)) = (get_min_max(), get_min_max()) cu.execute("INSERT INTO feature VALUES (NULL,%s,%s,%s,%s,%s)" % (mzMin, mzMax, rtMin, rtMax, get_lnk())) for i in range(20000): cu.execute("INSERT INTO spectrum VALUES (NULL,%s,%s,%s)" % (get_in_between(), get_in_between(), get_lnk())) cu.execute("COMMIT") cu.execute("ANALYZE")
def get_lnk(): return rand.randint(1, 2)
def get_min_max(): return sorted((rand.normalvariate(0.5, 0.004), rand.normalvariate(0.5, 0.004)))
def get_in_between(): return rand.normalvariate(0.5, 0.49)
def select(cu): sql = """ SELECT feature_id, spectrum_id FROM spectrum, feature WHERE lnk_feature = lnk_spectrum AND rt >= rtMin AND rt <= rtMax AND mz >= mzMin AND mz <= mzMax""" start = time.time() cu.execute(sql) print ("%s rows; %.2f seconds" % (len(cu.fetchall()), time.time() - start))
cu = apsw.Connection('foo.db').cursor() populate(cu) select(cu)

