class SummaryHistory(models.Model): version = models.IntegerField(editable=False) book = models.ForeignKey('Book') text = models.TextField()
class Meta: unique_together = ('version', 'book',)
def save(self, *args, **kwargs): # start with version 1 and increment it for each book current_version = SummaryHistory.objects.filter(book=self.book).order_by('-version')[:1] self.version = current_version[0].version + 1 if current_version else 1 super(SummaryHistory, self).save(*args, **kwargs)

class Book(models.Model):
    title = models.CharField(max_length=100)
    summary = models.TextField()

    def summary_history(self):
        return SummaryHistory.objects.filter(book=self).order_by('-version')

    def save(self, *args, **kwargs):
        super(Book, self).save(*args, **kwargs)
        # save summary history
        summary_history = self.summary_history()
        if not summary_history or self.summary != summary_history[0].text:
            newSummary = SummaryHistory(book=self, text=self.summary)
            newSummary.save()

