def walk(self,_root='/'):
    result=[]
    for child in self.children:
        path="%s%s/"%(_root,child.name) if child.children else "%s%s"%(_root,child.name)
        result.append((path,child))
        result.extend(child.walk(_root=path))

    return result  #result if _root != '/' else zip(*result)

