def towers_of_hanoi(count, source, via, destination): if count > 0: count -= 1 yield from towers_of_hanoi(count, source, destination, via) yield source, destination yield from towers_of_hanoi(count, via, source, destination)
def trace(start, *names): def tracefunc(frame, event, arg): if event == 'call': code = frame.f_code name = code.co_name if name in names: level = -start args = ', '.join(repr(frame.f_locals[name]) for name in code.co_varnames[:code.co_argcount]) while frame: frame = frame.f_back level += 1 print('{}{}.{}({})'.format( ' ' * (level * 4), os.path.splitext(os.path.basename(code.co_filename))[0], name, args)) return tracefunc sys.settrace(tracefunc)
if __name__ == '__main__': trace(3, 'main', 'towers_of_hanoi') main(3)

