    return str(self.value)

def __float__(self):
    return self.value

def __radd__(self, other):
    return other + self.value

import os, gtk, gobject from time import time, sleep from datetime import datetime
class HeartbeatTrayIcon(object): """ Use GTK to create an object in the system tray and manipulate icon shown if there is an issue. """ def __init__(self): self.statusIcon = gtk.StatusIcon()
def check(self): rx = os.path.getmtime("test") if (time() - rx) > (60*60*24): self.statusIcon.set_from_stock(gtk.STOCK_CANCEL) else: self.statusIcon.set_from_stock(gtk.STOCK_APPLY) self.statusIcon.set_tooltip("Last heartbeat received at %s" \ % datetime.fromtimestamp(int(rx)).strftime('%H:%M:%S %d-%m-%Y')) return True
def tray(self): gobject.idle_add(self.check) gtk.main()
if __name__ == "__main__": i = HeartbeatTrayIcon() i.tray()

