def step1(): rsaKey = RSA.importKey(open("./myKey.der", 'r')) print "Step 1: This is my rsa-key:\n%s" % rsaKey.exportKey()
def step2_encrypt(string): rsaKey = RSA.importKey(open("./myKey.der", 'r')) pkcs1CipherTmp = PKCS1_OAEP.new(rsaKey) encryptedString = pkcs1CipherTmp.encrypt(string) print "Step 2: encrypted %s is %s" % (string, encryptedString) return encryptedString
def step3_decrypt(encryptedString): rsaKey = RSA.importKey(open("./myKey.der", 'r')) pkcs1CipherTmp = PKCS1_OAEP.new(rsaKey) decryptedString = pkcs1CipherTmp.decrypt(encryptedString) print "Step 3: decryptedString %s is %s" % (encryptedString, decryptedString) return decryptedString
