def find(file, paragrapgh_number, search_string):
    paragraph = 'paragraph {}'.format(paragrapgh_number)
    for line in file:
        if line.find(paragraph) >= 0:
            break
    for line in file:
        if line.find(search_string) >=0:
            print('Found:', line)
        elif line.find('paragraph') >= 0:
            print('Not found')
            return

