import re

level = 0
for tok in re.finditer(r"\w+|[()]", s):
    tok = tok.group()
    if tok == "(":
        level += 1
    elif tok == ")":
        level -= 1
    else:
        print "%s%s" % (" "*level, tok)

def nodups(s):
    last = None
    for w in s:
        if w == last:
            continue
        yield w
        last = w

