root = tk.Tk() time_label = tk.Label(root, text='<unknown> seconds') volt_label = tk.Label(root, text='<unknown> volts') time_label.pack() volt_label.pack()
def DAQ(q): while True: q.put((time.time(), random.randrange(100))) time.sleep(2)
def update_data(queue, root): try: timestamp, volts = queue.get_nowait() except Empty: pass else: time_label.config(text='{:.1f} seconds'.format(timestamp)) volt_label.config(text='{:.4f} volts'.format(volts)) root.after(100, update_data, queue, root)
data_queue = Queue() t = threading.Thread(target=DAQ, args=(data_queue,)) t.daemon = True t.start() update_data(data_queue, root) root.mainloop()

def extracting_columns(specie, elements):
  species_vector=zeros(len(elements))
  print(specie)
  for (el,mul) in specie:
    species_vector[elements.index(el)]=mul
  return species_vector

def equations(specie, elements):
  vectors=[]
  for x in specie:
    formula = parse_formula(x)
    vector=extracting_columns(formula, elements)
    vectors.append(vector)

def equations(specie, elements):
    result = []
    for x in specie:
        formula = parse_formula(x)
        result.append(extracting_columns(formula, elements))
    return np.array(result)

import numpy as np
import re

def equations(specie, elements):
    result = []
    for x in specie:
        formula = parse_formula(x)
        result.append(extracting_columns(formula, elements))
    return np.array(result)

def extracting_columns(formula, elements):
    return [formula.get(e, 0) for e in elements]

def parse_formula(formula):
    elts = iter(re.split(r'([A-Z][a-z]*)',formula)[1:])
    return {element:toint(num) for element, num in zip(*[elts]*2)}

def toint(num):
    try:
        return int(num)
    except ValueError:
        return 1

