def dynamic_assertion(response, field, value):
    for item in response["items"]:
        for key in field:
            item = item[key] # go deeper into the nested dictionary
        if item != value:
            return False # raising an exception might be more Pythonic
    return True

class DictCounter(object): def __init__(self, list_of_ds): for k,v in list_of_ds[0].items(): self.__dict__[k] = collections.Counter([d[k] for d in list_of_ds])
>>> new_things = [{'test': 1, 'count': 1} for i in range(10)] >>> for i in new_things[0:5]: i['count']=2
>>> d = DictCounter(new_things) >>> d.count Counter({1: 5, 2: 5}) >>> d.test Counter({1: 10})

