def clean(items):
    clean = []
    for objs in items:
        nObj = {}
        for item, obj in objs.iteritems():
            if item != "tags":
                nObj[item] = obj
            else:
                nObj["tags"] = [n.lstrip() for n in obj]
        clean.append(nObj)
    return clean

