def determina_vencedor(t):
    if vencedor_coluna(t):
        return True

    if vencedor_linha(t)
        return True

    if vencedor_diagonal(t)
        return True

    return False

def determina_vencedor(t):
    for func in (vencedor_coluna, vencedor_linha, vencedor_diagonal):
        if func(t):
            return True
    return False

import numpy as np import collections import itertools as IT import matplotlib.pyplot as plt import timeit
def using_unique(x): y, inverse = np.unique(x, return_inverse=True) return np.column_stack((y, np.bincount(inverse)))
def using_counter(x): result = collections.Counter(x) return np.array(sorted(result.items()))
def using_ordered_dict(x): result = collections.OrderedDict() for item in x: result[item] = result.get(item,0)+1 return np.array(result.items())
def using_groupby(x): return np.array([(k, sum(1 for i in g)) for k, g in IT.groupby(x)])
