from decimal import Decimal import itertools
def encrypt(numbers_as_text, code): key = itertools.cycle(code[::-1])
num = Decimal(numbers_as_text)
power = 1 for _ in numbers_as_text: num += power * int(next(key)) power *= Decimal(10)
return num
