def overlap_split_list(l,s):
    l1 = []
    l2 = []
    for i in l:
        if i in s:
            l1.append(l2)
            l2 = []
        l2.append(i)
    if l2: l1.append(l2)
    return l1

def generator_overlap_split_list(l,s):
    l2 = []
    for i in l:
        if i in s:
            yield l2
            l2 = []
        l2.append(i)
    if l2: yield l2

