    return servers

import numpy

def dms_string_to_radians(dms_string):
    degrees = numpy.sum(numpy.fromstring(dms_string, sep='-') * 
            [1.0, 1/60.0, 1/3600.0])

    return numpy.radians(degrees)

print dms_string_to_radians('47-39-28')

import string, math
def dms_string_to_radians(dms_string):
dms = [float(x) for x in string.split(dms_string, '-')] degrees = sum( [scale * val for scale, val in zip([1.0, 1/60.0, 1/3600.0], dms)])
return math.radians(degrees)
print dms_string_to_radians('47-39-28')

from psycopg2.extensions import adapt as sqlescape import sqlalchemy from sqlalchemy import select from sqlalchemy.ext.compiler import compiles from sqlalchemy.sql.expression import ColumnClause
class MyFunc(ColumnClause): def __init__(self, *args, **kwargs): self.kwargs = kwargs super().__init__(*args)
@compiles(MyFunc) def compile_myfunc(element, compiler, **kw): s = ','.join("%s:=%s" % (k, sqlescape(v)) for k, v in element.kwargs.items()) return "%s(%s)" % (element.name, s)
def call(engine, func, **kwargs): return engine.execute(select([MyFunc(func, **kwargs)]))
