def parse_files(dataset_path,f): # please avoid using reserved words like file threads = range(0,16,2) dataset = csv.reader(open(dataset_path,'rb'), delimiter=',') results = [] for line in dataset: counter = {str(x):[] for x in range(0,8,2)} # map(lambda x:counter[line[x+1]].append(line[x]), threads) # map(lambda ...) is just a more pythonic way to write the following two lines for index in threads: counter[line[index+1]].append(line[index]) # now counter is something like #{'0': ['c', 'l'], '2': ['a', 'm'], '4': ['mi', 's'], '6': ['b', 'G']}
results.extend([','.join(v)+'='+k for k,v in counter.items()]) # in results, I'm appending something like this: # {'c,l=6', 'a,m=0', 'b,mi=2', 'G,s=4'}
sorted_results = sorted(dict(Counter(results)).iteritems(), key=operator.itemgetter(1), reverse=True) print '\n'.join(['The couple %s appears %d times'%el for el in sorted_results])
