def letter_grade(score):    
    if score < 60:
        print 'The grade is E'
    elif score < 70:
        print 'The grade is D'
    elif score < 80:
        print 'The grade is C'
    elif score < 90:
        print 'The grade is B'
    else:
        print 'The grade is A' 
    return score


