# Update the output image and display if needed out_all = np.zeros((256, 256))
def pool_initializer(out_queue_): # Setup out_queue as a global variable *in the worker subprocesses* global out_queue out_queue = out_queue_
def animate(): global out_all try: out_image = out_queue.get_nowait() except Queue.Empty: pass else: logger.info("Updating") out_all += out_image im.set_data(out_all) fig.canvas.draw() # redraw the canvas win.after(100, animate)
if __name__ == '__main__': out_queue = mp.Queue() logger.info("Starting pool") pool = mp.Pool(initializer=pool_initializer, initargs=(out_queue, )) work = [np.random.random((256, 256)) for f in range(20)] for o in work: pool.apply_async(do_work, [o]) pool.close()
