        "async" : true,
    },
});

def __getitem__(self, key):
    if isinstance(key, slice):
        return [self.list[i] for i in xrange(slice.start, slice.stop, slice.step)]
    return self.list[key]

def depends(*deps):
    def deco(func):
        def __new__(self):
            super(Dependency, self).__new__(self)
            return func()
        Dependency = type('Dependency', deps, {'__new__': __new__})
        return Dependency
    return deco


@depends(object)
def f():
    print 'using f'


@depends(f)
def g():
    print 'using g'


@depends(g, f)
def h():
    print 'using h'

h()
# using f
# using g
# using h

