def break_degree_wrap(values): values1, values2 = itertools.tee(values)
# Yield the first value yield next(values2)
for (prev_datetime, prev_val), (datetime, val) in itertools.izip(values1, values2):
# If the data wraps over the top if val > prev_val and val - prev_val > 180: yield (datetime, val - 360) yield (datetime, None) yield (prev_datetime, prev_val + 360)
# If the data wraps under the bottom elif val < prev_val and prev_val - val > 180: yield (datetime, val + 360) yield (datetime, None) yield (prev_datetime, prev_val - 360)
# Add each original value yield (datetime, val)

