def ParentSet(self, value):
  if not isinstance(value, basestring):
    raise TypeError('Parent name must be a string.')

  self._parent = value
  if ndb.Key(MyParent, value).get() is None:
    raise endpoints.NotFoundException('Parent %s does not exist.' % value)
  self.key = ndb.Key(MyParent, self._parent, MyModel, None)

  self._endpoints_query_info.ancestor = ndb.Key(MyParent, value)

@EndpointsAliasProperty(setter=ParentSet, required=True)
def parent(self):
  if self._parent is None and self.key is not None:
    self._parent = self.key.parent().string_id()
  return self._parent

class Knapsack(object):
    def __init__(self, seq, target):
        self.seq = seq
        self.target = target
        self.solution = None
    def solveIt(self):
        if self.target == 0 or self.target in self.seq:
            self.solution = (target,)
            return True, self.solution
        for r in range(len(self.seq),1,-1):
            for subset in combinations(self.seq, r):
                if sum(subset) == self.target:
                   self.solution = subset
                   return True, self.solution
        return False, ()

def __str__(self):
    if self.solution is None:
        self.solveIt()
    return '{}\n{}'.format(len(self.seq), 
                           ','.join(map(str, self.solution)))

def __str__(self):
    solved, solution = self.solveIt()
    return '{}\n{}'.format(len(self.seq), 
                           ','.join(map(str, solution)))

