def find( x, string, backward = False, ignore_case = False ): x = str(x) string = str(string) if ignore_case: x = x.lower() string = string.lower() str_list = [ i for i in string ] x_list = [ i for i in x ] if backward: x_list.reverse() str_list.reverse() x = ''.join(x_list) string = ''.join(str_list) lenx = len(x) ans = [] for i in range( len(str_list) - lenx ): if x == string[i:i+lenx]: ans.append( i ) return ans
def rfind( x, string, ignore_case = False): return find( x, string, backward = True, ignore_case = ignore_case )
print find('f','abcdefgacdfh') # [5, 10] print rfind('f','abcdefgacdfh') # [1, 6] print find(12,'aaa3331222aa12a') # [6, 12] print rfind(12,'aaa3331222aa12a') # [1, 7]

import functools
def None_is_dumb_and_does_not_deserve_to_enter_this_function(func):
    @functools.wraps(func)
    def new_func(*args,**kwargs):
        if None in args:
            raise ValueError("None's aren't welcome here")
        return func(*args,**kwargs)
    return new_func

@None_is_dumb_and_does_not_deserve_to_enter_this_function
def foo(a,b,c):
    """Docstring"""
    print a,b,c

foo(1,2,3)
print foo.__doc__
foo(None,'bar','baz')

import decorator

@decorator.decorator
def no_none(f,*args,**kwargs):
    if None in args:
        raise ValueError("None's aren't welcome here")
    return f(*args,**kwargs)

@no_none
def foo(a,b,c):
    """Docstring"""
    print a,b,c

foo(1,2,3)
print foo.__doc__

try:
    foo(None,'bar','baz')
except ValueError as e:
    print ('Good, raised ValueError')

try:
    foo("bar","baz",c=None)
except ValueError as e:
    print ('Good, raised ValueError')

