@register.filter(name='show_student')
def show_student(obj, d):
    show = True
    for key, val in d.iteritems():
        if hasattr(obj, key):
            obj_val = getattr(key, obj)
            if obj_val != val:
                show = False
                break
        else:
            show = False
            break
    return show

