def getTimeCode(fn):
    with open(fn, 'r') as f:
        for line in f:
            m = re.search(r'(\w)="(\d\d/\d\d/\d\d\d\d \d\d:\d\d:\d\d:\d\d\d)"', line)
            if m:
                if m.group(1) == 'StartTime':
                    # do something with m.group(2)
                elif m.group(1) == 'EndTime':
                    # do something with m.group(2)
                else:
                    # m.group(1) unknown

