def enum(*sequential, **named):
    """ Contruct an enum """
    enums = dict(zip(sequential, range(len(sequential))), **named)
    reverse = dict((value, key) for key, value in enums.items())
    key_names = enums.keys()
    enums['reverse_mapping'] = reverse        
    @classmethod
    def keys(klass):
       return key_names 
    enums['keys'] = keys
    return type('Enum', (), enums)

