import struct

def convert_temp (bytes):
    raw_temp = (bytes & 0xff00) >> 8
    raw_frac = (bytes & 0x00ff) >> 3

    a, b = struct.unpack('bb', '{}{}'.format(chr(raw_temp), chr(raw_frac)))
    return a + (0.03125 * b)

def parse_file():
  with open('SOMETHING.txt', 'r') as f:
    c1 = 0
    c2 = 0
    for i in f:
      w = i.split()
      c1 += len(w)
      for j in w:
        if j.endswith('LY'):
          c2 += 1
    return {'Total words': c1, 'Words ending with LY': c2}

