"""Module that monkey-patches json module when it's imported so JSONEncoder.default() automatically checks for a special "to_json()" method and uses it to encode the object if found. """ from json import JSONEncoder
def _default(self, obj): return getattr(obj.__class__, "to_json", _default.default)(obj)
_default.default = JSONEncoder().default # save unmodified default JSONEncoder.default = _default # replacement

import json
import make_json_serializable

class Foo(object):
    def __init__(self, name):
        self.name = name
    def to_json(self):  # new special method
        return "{u'name': %r}" % self.name.decode('utf-8')

foo = Foo('sazpaz')
print json.dumps(foo)  # "{u'name': u'sazpaz'}"

"""Module that imports the json module and monkey-patches it so JSONEncoder.default() automatically pickles any Python objects encountered that aren't standard JSON data types. """ from json import JSONEncoder import pickle
def _default(self, obj): return {'_python_object': pickle.dumps(obj)}
_default.default = JSONEncoder.default # save default JSONEncoder.default = _default # replacement

def as_python_object(dct):
    if '_python_object' in dct:
        return pickle.loads(str(dct['_python_object']))
    return dct

pyobj = loads(json_str, object_hook=as_python_object)

def new(inputfile,history_ends,
        historyjobs=historyjobs,targetjobs=targetjobs):

    # checking the file
    with open(inputfile,'r') as f:
        for line in f:
            job = map(int,line.split())
            (historyjobs if job[0]  < history_ends
             else targetjobs).append(job)

    while True:
        k = 0           
        zhlistsub = [i for i, element in enumerate(historyjobs)
                     if element[3:6] == targetjobs[k][3:6] ]
        if zhlistsub:
            listcreate(*quickzh(zhlistsub))
        tj00 = targetjobs[0][0]
        while True:
            if targetjobs[0][0]!=tj00:
                break
            historyjobs.append(targetjobs.pop(0))
        if len(targetjobs)==0:
            break

