def keyPressEvent(self, event):
        if event.key() in [16777235, 16777237, 16777234, 16777236, 16777220]: # Up, down, left, right, return.
            event.ignore()
        else:
            super(MyTextEdit, self).keyPressEvent(event)

def itercolumn(sheet, col, iStart=1):
    res = []
    while True:
        cellval = sheet.Cells(iStart, col).value
        if cellval == None:
            return res
        res.append(cellval)
        iStart += 1

def iterbyrange(sheet, col, start, end):
    res = []
    for i in range(start, end):
        res.append(sheet.Cells(i, col).value)
    return res

def func(row):
    xml = ['<item>']
    for field in row.index:
        xml.append('  <field name="{0}">{1}</field>'.format(field, row[field]))
    xml.append('</item>')
    return '\n'.join(xml)

