import random



def get_bet():
    bet = float('+inf')
    while bet > money:
        print("How much do you want to bet?")
        bet = int(raw_input("> "))
        if bet > money:
            bet_too_much()
    return bet

def bet_too_much():
    print("you do not have all the money. Please bet again.")
    raw_input("Press ENTER to continue")


def roulette_sim():
    money = 50
    losses = 0
    while True:
        bet = get_bet()
        won = red_or_black(bet)
        money += won
        losses -= won

