EOF = chr(4) import zmq
def file_sink(filename, url): """forward messages on zmq to a file""" socket = zmq.Context.instance().socket(zmq.PULL) socket.bind(url) written = 0 with open(filename, 'wb') as f: while True: chunk = socket.recv() if chunk == EOF: break f.write(chunk) written += len(chunk)
socket.close() return written

class FileProxy(object):
    """Proxy to a remote file over zmq"""
    def __init__(self, url):
        self.socket = zmq.Context.instance().socket(zmq.PUSH)
        self.socket.connect(url)

    def write(self, chunk):
        """write a chunk of bytes to the remote file"""
        self.socket.send(chunk)

