import numpy as np import itertools as it
def row_product(*arrays): lengths = np.array([x.shape[0] for x in arrays]) positions = np.cumsum(lengths)
ranges = np.arange(positions[-1]) ranges = np.split(ranges,positions[:-1])
total = np.concatenate((arrays),axis=0)
inds = np.fromiter(it.chain.from_iterable(it.product(*ranges)), np.int) inds = inds.reshape(-1, len(arrays))
return np.take(total, inds, axis=0)

