DIR = r'C:\Users\Documents\Congressional Hearings\NHTF Project\Test Set' HARD_WRAP = re.compile(r'\n(?! )') SPEAKERS = re.compile(r'^ (Mr.|Mrs.|Congressman|Congresswoman|Chairman|Chairwoman) ([a-zA-Z \-]{2,40})\.', re.MULTILINE) NAME = lambda m: '{0} {1}'.format(*m.groups()) Speaker = namedtuple('Speaker', ['name', 'name_start', 'name_end'])
def load_hearing_response(fname, split_on=' Present:'): with open(fname, 'rU') as inf: html = inf.read() txt = BeautifulSoup(html).get_text() return txt.rsplit(split_on, 1)[-1] # return everything after last occurrence of split_on
def un_hard_wrap(txt, reg=HARD_WRAP): return reg.sub('', txt)
def pairwise(iterable): a,b = tee(iterable) next(b, None) return izip(a, b)
def get_speeches(txt): speakers = [Speaker(NAME(sp), sp.start(), sp.end()) for sp in SPEAKERS.finditer(txt)] speakers.append(Speaker('', len(txt), None)) # tail sentinel for pairwise processing return [(this.name, txt[this.name_end:nxt.name_start]) for this,nxt in pairwise(speakers)]
def write_csv(fname, data, header=None): with open(fname, 'wb') as outf: out_csv = csv.writer(outf) if header is not None: out_csv.writerow(header) out_csv.writerows(data)
def main(): # get text of Congressional hearing responses txt = load_hearing_response(os.path.join(DIR, 'CHRG-107hhrg70750.htm')) txt = un_hard_wrap(txt) # break into speeches speeches = get_speeches(txt) # write (speaker, speech) pairs to a .csv file write_csv(os.path.join(DIR, 'CHRG-107hhrg70750.csv'), speeches, ['Speaker', 'Speech']) # write paragraphs of speeches to a .csv file paragraphs = ([para.strip()] for speaker,speech in speeches for para in speech.split('\n') if para.strip()) write_csv(os.path.join(DIR, 'Paragraphs.csv'), paragraphs, ['Paragraphs'])
if __name__=="__main__": main()

