def coords(split_image, totalpixels):
    xstop = totalpixels[0] - split_image[0]
    ystop = totalpixels[1] - split_image[1]
    return [(x, y) for x in range(0, xstop, split_image[0])
            for y in range(0, ystop, split_image[1])]

