BEGIN{idx=1}
NF==2 {
    if(key!=$1){
        print_and_clear();
    }
    data[idx]=$0;
    idx++;
    key=$1;
}
function print_and_clear()
{
    n=length(data);
    for(i=1; i<=n; i++){
        print data[i] " " n-i+1;
    }
    idx=1;
    delete data;
}
END{print_and_clear();}
