from functools import lru_cache

@lru_cache(16)  # cache 16 different `x` return values
def costly_function_a(x):
    print("costly_function_a has been called.")
    return x #Dummy operation.

@lru_cache(32)  # cache 32 different `x` return values
def costly_function_b(x):
    print("costly_function_b has been called.")
    return 5.*x #Dummy operation.

from functools import wraps

def cache_most_recent(func):
    cache = [None, None]
    @wraps(func)
    def wrapper(*args, **kw):
        if (args, kw) == cache[0]:
            return cache[1]
        cache[0] = args, kw
        cache[1] = func(*args, **kw)
        return cache[1]
    return wrapper

@cache_most_recent
def costly_function_a(x):
    print("costly_function_a has been called.")
    return x #Dummy operation.

@cache_most_recent
def costly_function_b(x):
    print("costly_function_b has been called.")
    return 5.*x #Dummy operation.

def get_non_base_10(s):
    #support for base 2,8,and 16
    if s.startswith("O") and s[1:].isdigit():
       return int(s[1:],8)
    elif s.startswith("0x") and s[2:].isdigit():
       return int(s[2:],16)
    elif s.startswith("0b") and s[2:].isdigit():
         return int(s[2:],2)

def get_number(s,decimal_separator=".",thousands_separator=","):
    if isinstance(s,basestring):
       temp_val = get_non_base_10(s)
       if temp_val is not None:
          return temp_val
       s = s.replace(decimal_separator,".").replace(thousands_separator,"")
    try:
       return float(s)
    except ValueError:
       return "nan"

def is_number(s,decimal_separator=".",thousands_separator=",",return_type="b"):
    numeric = get_number(s,decimal_separator,thousands_separator)
    return numeric if return_type != "b" else numeric != "nan"

def is_reverse(a,b):
    # copy b -- Not strictly necessary if you don't care about changing the inputs...
    c = b[:] 
    c.reverse()
    for i in range(len(a)):
        if a[i] != c[i]:
            return False
    return True

def controller(currentV, time):
    z = integrate.odeint(uderivs, currentV, time)
    return kp*(vr-currentV) + ki*z.squeeze()

def velocity(desired, theta, time):
    return integrate.odeint(vderivs, desired, time)

import matplotlib.pylab as plt import numpy as np import scipy.integrate as integrate
##Parameters kp = .5 #proportional gain ki = .1 #integral gain vr = 30 #desired velocity in m/s Tm = 190 #Max Torque in Nm wm = 420 #engine speed B = 0.4 #Beta an = 12 #at gear 4 p = 1.3 #air density Cd = 0.32 #Drag coefficient Cr = .01 #Coefficient of rolling friction A = 2.4 #frontal area
##Variables m = 18000.0 #weight v0 = 20. #starting velocity t = np.linspace(0, 20, 50) #time dt = .1 theta = np.radians(4) #Theta
def torque(v): return Tm * (1 - B*(an*v/wm - 1)**2)
def vderivs(v, t): ts = np.arange(0, t, dt) v1 = an * controller(v, ts) * torque(v) v2 = m*Cr*np.sign(v) v3 = 0.5*p*Cd*A*v**2 v4 = m*np.sin(theta) vtot = v1+v2+v3+v4*(ts>=10) return vtot/m
def uderivs(v, t): return vr - v
def controller(currentV, time): z = integrate.odeint(uderivs, currentV, time) return kp*(vr-currentV) + ki*z.squeeze()
def velocity(desired, theta, time): return integrate.odeint(vderivs, desired, time)
