def kmax(col):
    key = int if all(x.isdigit() for x in col) else str
    return max(col, key=key)

print kmax(['1','40','5','520','1025']) # 1025
print kmax(['foo','bar','40','baz']) # foo

from collections import defaultdict

class Index:
    def __init__(self, stringlist):
        self.index = defaultdict(list)
        for string in stringlist:
            for word in string.split():
                self.index[word].append(string)

    def strings_containing(self, word):
        return self.index[word]

from Tkinter import * from socket import * port = 7142
on = '02 00 00 00 00' off = '02 01 00 00 00'
def callback_power_on(data, host): if not host: print "No host given!" return print "power on!" connection = socket(AF_INET, SOCK_STREAM) connection.connect((host, port)) connection.sendall(add_checksum(data)) connection.close()
def callback_power_off(data, host): if not host: print "No host given!" return print "power off!" connection = socket(AF_INET, SOCK_STREAM) connection.connect((host, port)) connection.sendall(add_checksum(data)) connection.close()
def add_checksum(s): result = [] acc = 0 for hexcode in s.split(): code = int(hexcode, 16) acc += code result.append(chr(code)) result.append(chr(acc)) return ''.join(result)
