def powerprod(iterable):
    s = list(iterable)
    for r in itertools.count(1):
        for c in itertools.product(s, repeat=r):
            yield c

from itertools import product, islice
def max_sum_n_pow_lower_x(x, n): """ returns tuple of number of summand and maximal sum of form `n` + `n`**2 + `n`**3 not greater than `x` """ i, c, s = 1, 0, 0 while s < x: i *= n c += 1 s += i return c-1, s-i
def get_nth_pow(iterable, n): l = list(iterable) repeat, start_from = max_sum_n_pow_lower_x(n, len(l)) prod = itertools.product(l, repeat=repeat+1) return ''.join(list(islice(prod, n-start_from))[-1])

