def get_char(is_valid = lambda c: True):
    while True:
        c = raw_input('enter a character > ')
        if is_valid(c):
            keep = raw_input('do you want to use {0}? (Y/N)> '.format(c)).lower()
            if 'n' in keep:
                continue # go back to While True line
            return c
        print('{0} is not a valid character'.format(c))

