import operator
def differ(w1, w2):
    return len(w1) == len(w2) and sum(map(operator.ne, w1, w2)) == 1

import operator, itertools
def differ(w1, w2):
    it = itertools.imap(operator.ne, w1, w2)
    return len(w1) == len(w2) and any(it) and not any(it)

