class Point(object):
    def __init__(self, x=0.0, y=0.0):
        self.x = x
        self.y = y

    def __str__(self):
        return '{0:.2f}, {1:.2f}'.format(self.x, self.y)

    def print_point(self):
        print('%.1d , %1.d' % (self.x, self.y))


