    class Calculator:
        def __init__(self,x,y):
            self.x = int(x)
            self.y = int(y)
        def add(self):
            return self.x + self.y
        def sub(self):
            return self.x - self.y
        def multi(self):
            return self.x * self.y
        def div(self):
            return self.x / self.y

        def convert(self,a,b):
            try:
                int_a = int(a)
                int_b = int(b)
            except ValueError:
                int_a = float(a)
                int_b = float(b)
            return int_a,int_b

