def streamInYAML(stream):
	y = stream.readline()
	cont = 1
	while cont:
		l = stream.readline()
		if len(l) == 0:
			cont = 0
		else:
			if l.startswith(' '):
				y = y + l
			else:
				yield yaml.load(y)
				y = l

